import { cache } from "../cache.js";
export function Category(options) {
    return function (target) {
        if (cache.size == 0) {
            console.error("There is no instance of a amethystBot");
            return;
        }
        let bot;
        if (cache.size == 1)
            bot = cache.first();
        else if (options.botCacheNumber)
            bot = cache.get(options.botCacheNumber);
        else
            bot = cache.first();
        bot.amethystUtils.createCategory(options);
    };
}
